INSERT INTO `news` (`id`, `category_id`, `title`, `image`, `posted_date`, `content`, `description`, `author`, `views`, `created_at`, `updated_at`, `deleted_at`, `status`) VALUES
(7, 2, 'Chân váy hoa cổ điển trở lại, đẹp từ công sở đến đường phố', 'baiviet7.webp', '2025-06-05', '<div class=\"detail-content afcbc-body\" data-role=\"content\" itemprop=\"articleBody\" data-io-article-url=\"https://thanhnien.vn/thoi-trang-tre/chan-vay-hoa-co-dien-tro-lai-dep-tu-cong-so-den-duong-pho-185250602144618644.htm\">                     <div data-check-position=\"body_start\"></div>                                         <p class=\"\">Bất kể bầu trời hôm nay bừng sáng ánh nắng hay u ám mây đen, một chiếc <a class=\"link-inline-content\" href=\"https://thanhnien.vn/chan-vay-hoa-don-tim-cac-nang-tho-ngay-he-185250524141439493.htm\" target=\"_blank\" title=\"chân váy hoa\" data-rel=\"follow\">chân váy hoa</a> xếp ly dáng xòe nhẹ nhàng kết hợp áo vải mềm mại đều có thể giúp nàng \"nâng mood\". Tinh thần tươi vui và lạc quan là bí quyết quan trọng nhất tạo nên sự tự tin và thần thái rạng ngời của quý cô.</p><figure class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><a href=\"https://images2.thanhnien.vn/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-2-17488505025791689148641.jpg\" data-fancybox=\"img-lightbox\" title=\"Bảng màu cam đất, nâu đất và họa tiết hoa ấn tượng kết hợp áo tay ngắn thoáng mát, trẻ trung mang vẻ đẹp duy mỹ cổ điển đầy cuốn hút\" target=\"_blank\" class=\"detail-img-lightbox\"><img data-author=\"\" src=\"https://images2.thanhnien.vn/thumb_w/640/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-2-17488505025791689148641.jpg\" id=\"img_160053180304850944\" w=\"1365\" h=\"2048\" alt=\" - Ảnh 1.\" title=\" - Ảnh 1.\" rel=\"lightbox\" photoid=\"160053180304850944\" data-original=\"https://images2.thanhnien.vn/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-2-17488505025791689148641.jpg\" type=\"photo\" style=\"max-width:100%;\" width=\"\" height=\"\" data-is-alt-modified=\"true\" class=\"lightbox-content\" data-adbro-processed=\"true\"></a></div><figcaption class=\"PhotoCMS_Caption\"><p data-placeholder=\"Nhập chú thích ảnh\" class=\"\">Bảng màu cam đất, nâu đất và họa tiết hoa ấn tượng kết hợp áo tay ngắn thoáng mát, trẻ trung mang vẻ đẹp duy mỹ cổ điển đầy cuốn hút</p></figcaption><div class=\"PhotoCMS_Author\"><p data-placeholder=\"Nhập tác giả\" class=\"\">ẢNH: CLASSIC LADY</p></div></figure><h2 class=\"\">Chân váy hoa dáng dài - nét lãng mạn, cổ tích giữa đời thực</h2><p class=\"\">Không quá phô trương như kiểu trang phục tiệc tùng hay những chiếc đầm bung xòe đầy màu sắc và họa tiết, những thiết kế chân váy in hoa duyên dáng và nhẹ nhàng mang đến làn gió mới, đưa nàng vào thế giới của những cô gái mộng mơ, yêu thích vẻ đẹp lãng mạn nhuốm màu cổ tích.</p><p class=\"\">Chân váy hoa dáng chữ A, <a class=\"seo-suggest-link link-inline-content\" href=\"https://thanhnien.vn/chan-vay-xep-ly.html\" target=\"_blank\" title=\"chân váy xếp ly\">chân váy xếp ly</a> in hoa lá kết hợp cùng áo sơ mi, áo kiểu thanh lịch làm nên những bản phối cổ điển và lãng mạn, vừa chỉn chu vừa trang nhã. Trong khi đó các tín đồ đường phố hướng đến tinh thần trẻ trung và táo bạo hơn với những cặp đôi váy hoa và bra top, áo sơ mi oversized kẻ sọc và chân váy lụa in họa tiết hoa.</p><figure class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><a href=\"https://images2.thanhnien.vn/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-1-17488505025441433632010.jpg\" data-fancybox=\"img-lightbox\" title=\"Mỗi cặp đôi áo và chân váy họa tiết lại gợi lên những cảm xúc khó tả, nhắc nhớ về thời thanh xuân hay là hình ảnh trữ tình của thời trang mang phong cách vintage đầy hoài niệm\" target=\"_blank\" class=\"detail-img-lightbox\"><img data-author=\"\" src=\"https://images2.thanhnien.vn/thumb_w/640/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-1-17488505025441433632010.jpg\" id=\"img_160053180456247296\" w=\"1365\" h=\"2048\" alt=\" - Ảnh 2.\" title=\" - Ảnh 2.\" rel=\"lightbox\" photoid=\"160053180456247296\" data-original=\"https://images2.thanhnien.vn/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-1-17488505025441433632010.jpg\" type=\"photo\" style=\"max-width:100%;\" width=\"\" height=\"\" data-is-alt-modified=\"true\" class=\"lightbox-content\"></a></div><figcaption class=\"PhotoCMS_Caption\"><p data-placeholder=\"Nhập chú thích ảnh\" class=\"\">Mỗi cặp đôi áo và chân váy họa tiết lại gợi lên những cảm xúc khó tả, nhắc nhớ về thời thanh xuân hay là hình ảnh trữ tình của thời trang mang <a class=\"link-inline-content\" href=\"https://thanhnien.vn/goi-nho-phong-cach-vintage-cung-chiec-vay-midi-sang-trong-giau-nu-tinh-185250408111808667.htm\" title=\"phong cách vintage\" data-rel=\"follow\">phong cách vintage</a> đầy hoài niệm</p></figcaption><div class=\"PhotoCMS_Author\"><p data-placeholder=\"Nhập tác giả\">ẢNH: CLASSIC LADY</p></div></figure><figure class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><a href=\"https://images2.thanhnien.vn/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-3-1748850502613338638735.jpg\" data-fancybox=\"img-lightbox\" title=\"Phụ kiện thắt lưng da, giày da cổ điển, mũ cói, vòng cổ ngọc trai... hoàn hảo cho các bản phối cùng chân váy hoa\" target=\"_blank\" class=\"detail-img-lightbox\"><img data-author=\"\" src=\"https://images2.thanhnien.vn/thumb_w/640/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-3-1748850502613338638735.jpg\" id=\"img_160053179828797440\" w=\"2048\" h=\"1365\" alt=\" - Ảnh 3.\" title=\" - Ảnh 3.\" rel=\"lightbox\" photoid=\"160053179828797440\" data-original=\"https://images2.thanhnien.vn/528068263637045248/2025/6/2/chan-vay-hoa-classic-lady-6-3-1748850502613338638735.jpg\" type=\"photo\" style=\"max-width:100%;\" width=\"\" height=\"\" data-is-alt-modified=\"true\" class=\"lightbox-content\"></a></div><figcaption class=\"PhotoCMS_Caption\"><p data-placeholder=\"Nhập chú thích ảnh\" class=\"\">Phụ kiện thắt lưng da, giày da cổ điển, mũ cói, vòng cổ ngọc trai... hoàn hảo cho các bản phối cùng chân váy hoa</p></figcaption><div class=\"PhotoCMS_